function file_check(obj, action_url, companyId, id) {
    var businessLicenes = obj.value;
    this.AllowExt = ".jpg,.jpeg,.gif,.png,.pdf", this.FileExt = businessLicenes.substr(businessLicenes.lastIndexOf(".")).toLowerCase(), 0 != this.AllowExt && -1 == this.AllowExt.indexOf(this.FileExt) ? errorTips("只支持jpg、png、jpeg、gif、pdf格式文件，请重新上传") : $.ajaxFileUpload({
        url: action_url,
        secureuri: !1,
        fileElementId: id,
        data: {companyId: companyId},
        dataType: "post",
        success: function (result) {
            window.location.href = result
        }
    })
}

$(function () {
    $(".errorTips").click(function () {
        errorTips("上传附件格式错误!")
    })
});